// 引入jQuery  const $ = require('jquery');    // 问题function printProblemMessage() {    var q = '一块蛋糕在森林里迷路了，是谁鼓励它走出来？' 
  console.log(q)}  

// 答案
function printAnswerMessage() {  
  // 例如，在DOM加载完成后打印欢迎消息  
  var a = '是猪，因为朱古力蛋糕。' 
  console.log(a)
}   // 暴露函数作为模块的公开API  module.exports = {
	printProblemMessage,
	printAnswerMessage
}; 