// 引入jQuery  const $ = require('jquery');    // 问题function printProblemMessage() {    var q = '妖和魔住在哪里？' 
  console.log(q)}  

// 答案
function printAnswerMessage() {  
  // 例如，在DOM加载完成后打印欢迎消息  
  var a = '钢琴和邮箱。因为刚擒（钢琴）住了几个妖，又降（邮箱）住了几个魔。' 
  console.log(a)
}   // 暴露函数作为模块的公开API  module.exports = {
	printProblemMessage,
	printAnswerMessage
}; 