// 引入jQuery  const $ = require('jquery');    // 问题function printProblemMessage() {    var q = '你知道海为什么是蓝色的吗？' 
  console.log(q)}  

// 答案
function printAnswerMessage() {  
  // 例如，在DOM加载完成后打印欢迎消息  
  var a = '因为海里的鱼在吐泡泡 blue blue blue~。' 
  console.log(a)
}   // 暴露函数作为模块的公开API  module.exports = {
	printProblemMessage,
	printAnswerMessage
}; 